import React, { useState, useRef } from "react";

const GraphVisualizer = () => {
  const [nodes, setNodes] = useState([]);
  const [draggingId, setDraggingId] = useState(null);
  const svgRef = useRef();

  const handleCanvasClick = (e) => {
    const bounds = svgRef.current.getBoundingClientRect();
    const x = e.clientX - bounds.left;
    const y = e.clientY - bounds.top;
    const newNode = {
      id: Date.now().toString(),
      x,
      y,
    };
    setNodes([...nodes, newNode]);
  };

  const handleMouseDown = (id) => {
    setDraggingId(id);
  };

  const handleMouseMove = (e) => {
    if (!draggingId) return;

    const bounds = svgRef.current.getBoundingClientRect();
    const x = e.clientX - bounds.left;
    const y = e.clientY - bounds.top;

    setNodes((prev) =>
      prev.map((node) =>
        node.id === draggingId ? { ...node, x, y } : node
      )
    );
  };

  const handleMouseUp = () => {
    setDraggingId(null);
  };

  return (
    <div className="p-4 w-full h-[80vh]">
      <h2 className="text-xl font-bold text-white mb-4">Graph Visualizer</h2>
      <svg
        ref={svgRef}
        onClick={handleCanvasClick}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        className="w-full h-full border rounded bg-gray-800"
      >
        {/* Draw nodes */}
        {nodes.map((node) => (
          <circle
            key={node.id}
            cx={node.x}
            cy={node.y}
            r="20"
            fill="#4F46E5"
            stroke="#fff"
            strokeWidth="2"
            onMouseDown={(e) => {
              e.stopPropagation();
              handleMouseDown(node.id);
            }}
          />
        ))}
      </svg>
    </div>
  );
};

export default GraphVisualizer;
