import React, { useState } from "react";

function TreeNode(val) {
  this.val = val;
  this.left = null;
  this.right = null;
}

const TreeVisualizer = () => {
  const [root, setRoot] = useState(null);
  const [value, setValue] = useState("");
  const [searchValue, setSearchValue] = useState("");
  const [deleteValue, setDeleteValue] = useState("");
  const [foundNode, setFoundNode] = useState(null);
  const insert = (node, val) => {
    if (!node) return new TreeNode(val);
    if (val < node.val) {
      node.left = insert(node.left, val);
    } else {
      node.right = insert(node.right, val);
    }
    return node;
  };

  const handleInsert = () => {
    if (!value) return;
    const num = parseInt(value);
    if (isNaN(num)) {
      alert("Please enter a number");
      return;
    }
    const newRoot = insert(root, num);
    setRoot({ ...newRoot }); // trigger re-render
    setValue("");
  };
  const handleSearch = () => {
  const num = parseInt(searchValue);
  if (isNaN(num)) {
    alert("Please enter a valid number");
    return;
  }

  const found = search(root, num);
  setFoundNode(found ? num : null);
};

const search = (node, val) => {
  if (!node) return false;
  if (node.val === val) return true;
  if (val < node.val) return search(node.left, val);
  return search(node.right, val);
};
const handleDelete = () => {
  const num = parseInt(deleteValue);
  if (isNaN(num)) {
    alert("Please enter a valid number");
    return;
  }

  const newRoot = deleteNode(root, num);
  setRoot({ ...newRoot });
  setDeleteValue("");
};

const deleteNode = (node, val) => {
  if (!node) return null;
  if (val < node.val) {
    node.left = deleteNode(node.left, val);
  } else if (val > node.val) {
    node.right = deleteNode(node.right, val);
  } else {
    if (!node.left) return node.right;
    if (!node.right) return node.left;

    let minLargerNode = node.right;
    while (minLargerNode.left) minLargerNode = minLargerNode.left;
    node.val = minLargerNode.val;
    node.right = deleteNode(node.right, minLargerNode.val);
  }
  return node;
};
  const renderTree = (node) => {
    if (!node) return null;
    return (
      <div className="flex flex-col items-center">
        <div className="bg-blue-600 text-white px-4 py-2 rounded mb-2">{node.val}</div>
        <div className="flex gap-4">
          {renderTree(node.left)}
          {renderTree(node.right)}
        </div>
      </div>
    );
  };

  return (
    <div className="bg-gray-800 p-6 rounded shadow-lg w-full max-w-xl text-center text-white">
      <h2 className="text-2xl font-bold mb-4">Binary Tree Visualizer</h2>
      <div className="flex justify-center items-center gap-2 mb-4">
        <input
          type="text"
          value={value}
          onChange={(e) => setValue(e.target.value)}
          placeholder="Enter value"
          className="px-2 py-1 rounded text-black"
        />
        <button
          onClick={handleInsert}
          className="bg-green-600 px-4 py-2 rounded hover:bg-green-700"
        >
          Insert
        </button>
        <div className="flex gap-2 mb-2">
  <input
    type="text"
    placeholder="Search value"
    value={searchValue}
    onChange={(e) => setSearchValue(e.target.value)}
    className="px-2 py-1 rounded text-black"
  />
  <button
    onClick={handleSearch}
    className="bg-yellow-500 px-4 py-2 rounded hover:bg-yellow-600"
  >
    Search
  </button>
</div>

<div className="flex gap-2 mb-4">
  <input
    type="text"
    placeholder="Delete value"
    value={deleteValue}
    onChange={(e) => setDeleteValue(e.target.value)}
    className="px-2 py-1 rounded text-black"
  />
  <button
    onClick={handleDelete}
    className="bg-red-600 px-4 py-2 rounded hover:bg-red-700"
  >
    Delete
  </button>
</div>

{foundNode !== null && (
  <p className="text-green-400 mt-2">Value {foundNode} found in the tree âœ…</p>
)}

      </div>
      {renderTree(root)}
    </div>
  );
};

export default TreeVisualizer;
