import React, { useState } from "react";
import { motion } from "framer-motion";

const MinHeapVisualizer = () => {
  const [heap, setHeap] = useState([]);

  const insert = (val) => {
    const newHeap = [...heap, val];
    let i = newHeap.length - 1;
    while (i > 0) {
      const parent = Math.floor((i - 1) / 2);
      if (newHeap[parent] > newHeap[i]) {
        [newHeap[parent], newHeap[i]] = [newHeap[i], newHeap[parent]];
        i = parent;
      } else break;
    }
    setHeap(newHeap);
  };

  const deleteMin = () => {
    if (heap.length === 0) return;
    const newHeap = [...heap];
    const last = newHeap.pop();
    if (newHeap.length === 0) {
      setHeap([]);
      return;
    }
    newHeap[0] = last;
    let i = 0;
    while (true) {
      let left = 2 * i + 1;
      let right = 2 * i + 2;
      let smallest = i;

      if (left < newHeap.length && newHeap[left] < newHeap[smallest]) {
        smallest = left;
      }
      if (right < newHeap.length && newHeap[right] < newHeap[smallest]) {
        smallest = right;
      }
      if (smallest !== i) {
        [newHeap[i], newHeap[smallest]] = [newHeap[smallest], newHeap[i]];
        i = smallest;
      } else break;
    }
    setHeap(newHeap);
  };

  const renderTree = (index) => {
    if (index >= heap.length) return null;
    return (
      <div className="flex flex-col items-center">
        <motion.div
          layout
          className="bg-green-600 text-white px-4 py-2 rounded mb-2"
        >
          {heap[index]}
        </motion.div>
        <div className="flex gap-4">
          {renderTree(2 * index + 1)}
          {renderTree(2 * index + 2)}
        </div>
      </div>
    );
  };

  return (
    <div className="p-6 text-white bg-gray-900 min-h-screen">
      <h1 className="text-3xl font-bold mb-4 text-center">Min-Heap Visualizer</h1>
      <div className="flex justify-center gap-4 mb-4">
        <input
          type="number"
          className="text-black px-2 py-1 rounded"
          placeholder="Enter value"
          onKeyDown={(e) => {
            if (e.key === "Enter") insert(parseInt(e.target.value));
          }}
        />
        <button
          onClick={deleteMin}
          className="bg-red-600 px-4 py-2 rounded hover:bg-red-700"
        >
          Delete Min
        </button>
      </div>

      <div className="flex justify-center overflow-auto">
        {renderTree(0)}
      </div>

      <div className="mt-8">
        <h2 className="text-xl font-semibold mb-2">Array Representation:</h2>
        <div className="flex flex-wrap gap-2">
          {heap.map((val, idx) => (
            <div
              key={idx}
              className="px-3 py-1 bg-blue-500 rounded shadow-md"
            >
              {val}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default MinHeapVisualizer;
