import React, { useState } from "react";
import StackVisualizer from "./components/StackVisualizer";
import QueueVisualizer from "./components/QueueVisualizer";

function App() {
  const [currentView, setCurrentView] = useState("stack");

  return (
    <div className="min-h-screen bg-gray-900 text-white flex flex-col items-center p-6">
      <h1 className="text-4xl font-bold mb-4">DSA Visualizer</h1>

      <div className="flex gap-4 mb-8">
        <button
          onClick={() => setCurrentView("stack")}
          className={`px-4 py-2 rounded ${
            currentView === "stack" ? "bg-blue-600" : "bg-gray-700"
          }`}
        >
          Stack
        </button>
        <button
          onClick={() => setCurrentView("queue")}
          className={`px-4 py-2 rounded ${
            currentView === "queue" ? "bg-blue-600" : "bg-gray-700"
          }`}
        >
          Queue
        </button>
      </div>

      {currentView === "stack" && <StackVisualizer />}
      {currentView === "queue" && <QueueVisualizer />}
    </div>
  );
}

export default App;
