import React, { useState } from "react";

const StackVisualizer = () => {
  const [stack, setStack] = useState([]);
  const [inputValue, setInputValue] = useState("");

  const handlePush = () => {
    if (inputValue.trim() !== "") {
      setStack([...stack, inputValue]);
      setInputValue("");
    }
  };

  const handlePop = () => {
    if (stack.length > 0) {
      const newStack = [...stack];
      newStack.pop();
      setStack(newStack);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900 text-white p-4">
      <h1 className="text-3xl font-bold mb-6">ðŸ§± Stack Visualizer</h1>

      <div className="flex gap-2 mb-4">
        <input
          type="text"
          className="px-3 py-2 text-black rounded-md focus:outline-none"
          placeholder="Enter value"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
        <button
          className="bg-green-600 hover:bg-green-700 px-4 py-2 rounded"
          onClick={handlePush}
        >
          Push
        </button>
        <button
          className="bg-red-600 hover:bg-red-700 px-4 py-2 rounded"
          onClick={handlePop}
        >
          Pop
        </button>
      </div>

      <div className="flex flex-col-reverse items-center gap-2 mt-6">
        {stack.map((item, index) => (
          <div
            key={index}
            className="w-32 text-center py-2 bg-blue-500 rounded shadow-md"
          >
            {item}
          </div>
        ))}
      </div>
    </div>
  );
};

export default StackVisualizer;
