import React, { useState, useEffect } from 'react';

const ALGORITHMS = ['Bubble Sort', 'Selection Sort', 'Insertion Sort'];

function SortingVisualizer() {
  const [array, setArray] = useState([]);
  const [speed, setSpeed] = useState(100);
  const [selectedAlgo, setSelectedAlgo] = useState(ALGORITHMS[0]);
  const [isSorting, setIsSorting] = useState(false);

  useEffect(() => {
    generateArray();
  }, []);

  const generateArray = () => {
    if (isSorting) return;
    const arr = Array.from({ length: 30 }, () => Math.floor(Math.random() * 300) + 20);
    setArray(arr);
  };

  const sleep = (ms) => new Promise((resolve) => setTimeout(resolve, ms));

  const bubbleSort = async () => {
    setIsSorting(true);
    const arr = [...array];
    for (let i = 0; i < arr.length; i++) {
      for (let j = 0; j < arr.length - i - 1; j++) {
        if (arr[j] > arr[j + 1]) {
          [arr[j], arr[j + 1]] = [arr[j + 1], arr[j]];
          setArray([...arr]);
          await sleep(speed);
        }
      }
    }
    setIsSorting(false);
  };

  const selectionSort = async () => {
    setIsSorting(true);
    const arr = [...array];
    for (let i = 0; i < arr.length; i++) {
      let minIdx = i;
      for (let j = i + 1; j < arr.length; j++) {
        if (arr[j] < arr[minIdx]) {
          minIdx = j;
        }
      }
      [arr[i], arr[minIdx]] = [arr[minIdx], arr[i]];
      setArray([...arr]);
      await sleep(speed);
    }
    setIsSorting(false);
  };

  const insertionSort = async () => {
    setIsSorting(true);
    const arr = [...array];
    for (let i = 1; i < arr.length; i++) {
      let key = arr[i];
      let j = i - 1;
      while (j >= 0 && arr[j] > key) {
        arr[j + 1] = arr[j];
        j--;
        setArray([...arr]);
        await sleep(speed);
      }
      arr[j + 1] = key;
      setArray([...arr]);
      await sleep(speed);
    }
    setIsSorting(false);
  };

  const handleSort = () => {
    if (isSorting) return;
    if (selectedAlgo === 'Bubble Sort') bubbleSort();
    else if (selectedAlgo === 'Selection Sort') selectionSort();
    else if (selectedAlgo === 'Insertion Sort') insertionSort();
  };

  return (
    <div className="w-full flex flex-col items-center">
      <h2 className="text-2xl font-semibold mb-4">Sorting Algorithm Visualizer</h2>
      
      <div className="flex gap-4 mb-4">
        <select
          className="bg-gray-700 text-white px-3 py-1 rounded"
          value={selectedAlgo}
          onChange={(e) => setSelectedAlgo(e.target.value)}
          disabled={isSorting}
        >
          {ALGORITHMS.map((algo) => (
            <option key={algo} value={algo}>{algo}</option>
          ))}
        </select>

        <input
          type="range"
          min="10"
          max="500"
          value={speed}
          onChange={(e) => setSpeed(Number(e.target.value))}
          disabled={isSorting}
          className="w-40"
        />

        <button onClick={generateArray} className="bg-blue-600 px-4 py-1 rounded" disabled={isSorting}>Reset</button>
        <button onClick={handleSort} className="bg-green-600 px-4 py-1 rounded" disabled={isSorting}>Sort</button>
      </div>

      <div className="flex items-end h-80 w-full max-w-5xl border border-gray-700 p-2 bg-gray-800 rounded">
        {array.map((val, idx) => (
          <div
            key={idx}
            style={{ height: `${val}px` }}
            className="bg-teal-400 w-2 mx-0.5 rounded-t"
          />
        ))}
      </div>
    </div>
  );
}

export default SortingVisualizer;
