import React, { useState } from "react";

function Node(value) {
  this.value = value;
  this.next = null;
}

const LinkedListVisualizer = () => {
  const [head, setHead] = useState(null);
  const [input, setInput] = useState("");
  const [renderList, setRenderList] = useState([]);

  const updateRenderList = (node) => {
    const list = [];
    let current = node;
    while (current) {
      list.push(current.value);
      current = current.next;
    }
    setRenderList(list);
  };

  const insert = () => {
    if (!input) return;
    const newNode = new Node(input);
    if (!head) {
      setHead(newNode);
      updateRenderList(newNode);
    } else {
      let current = head;
      while (current.next) {
        current = current.next;
      }
      current.next = newNode;
      updateRenderList(head);
    }
    setInput("");
  };

  const reset = () => {
    setHead(null);
    setRenderList([]);
  };

  return (
    <div className="w-full max-w-md bg-gray-800 p-6 rounded shadow-lg text-center">
      <h2 className="text-2xl font-bold mb-4 text-white">Linked List Visualizer</h2>
      <div className="flex gap-2 mb-4">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          className="flex-1 p-2 rounded text-black"
          placeholder="Enter value"
        />
        <button onClick={insert} className="bg-green-600 px-4 py-2 rounded hover:bg-green-700">
          Insert
        </button>
        <button onClick={reset} className="bg-red-600 px-4 py-2 rounded hover:bg-red-700">
          Reset
        </button>
      </div>
      <div className="flex items-center justify-center flex-wrap gap-2 mt-4">
        {renderList.map((val, idx) => (
          <div key={idx} className="bg-blue-600 px-4 py-2 rounded flex items-center gap-2">
            <span>{val}</span>
            {idx < renderList.length - 1 && <span>➡️</span>}
          </div>
        ))}
      </div>
    </div>
  );
};

export default LinkedListVisualizer;
