import React, { useState } from "react";

function TreeNode(val) {
  this.val = val;
  this.left = null;
  this.right = null;
}

const TreeVisualizer = () => {
  const [root, setRoot] = useState(null);
  const [value, setValue] = useState("");
  const [isBST, setIsBST] = useState(true); // Toggle between BST and General Tree
  const deleteNode = (root, key) => {
  if (!root) return null;

  if (key < root.val) {
    root.left = deleteNode(root.left, key);
  } else if (key > root.val) {
    root.right = deleteNode(root.right, key);
  } else {
    // Node with only one child or no child
    if (!root.left) return root.right;
    if (!root.right) return root.left;

    // Node with two children: Get inorder successor (smallest in the right subtree)
    let temp = root.right;
    while (temp.left) temp = temp.left;

    root.val = temp.val;
    root.right = deleteNode(root.right, temp.val);
  }

  return root;
};
  // Insert for BST
  const insertBST = (node, val) => {
    if (!node) return new TreeNode(val);
    if (val < node.val) {
      node.left = insertBST(node.left, val);
    } else {
      node.right = insertBST(node.right, val);
    }
    return node;
  };

  // Insert for General Binary Tree (level order)
  const insertGeneral = (node, val) => {
    if (!node) return new TreeNode(val);
    const queue = [node];
    while (queue.length) {
      const current = queue.shift();
      if (!current.left) {
        current.left = new TreeNode(val);
        break;
      } else queue.push(current.left);

      if (!current.right) {
        current.right = new TreeNode(val);
        break;
      } else queue.push(current.right);
    }
    return node;
  };

  const handleInsert = () => {
    if (!value) return;
    const num = parseInt(value);
    if (isNaN(num)) {
      alert("Please enter a number");
      return;
    }
    const newRoot = isBST ? insertBST(root, num) : insertGeneral(root, num);
    setRoot({ ...newRoot });
    setValue("");
  };

  const renderTree = (node) => {
    if (!node) return null;
    return (
      <div className="flex flex-col items-center">
        <div className="bg-blue-600 text-white px-4 py-2 rounded mb-2">{node.val}</div>
        <div className="flex gap-4">
          {renderTree(node.left)}
          {renderTree(node.right)}
        </div>
      </div>
    );
  };

  return (
    <div className="bg-gray-800 p-6 rounded shadow-lg w-full max-w-xl text-center text-white">
      <h2 className="text-2xl font-bold mb-4">Binary Tree Visualizer</h2>

      {/* Toggle Buttons */}
      <div className="flex justify-center gap-4 mb-4">
        <button
          onClick={() => setIsBST(true)}
          className={`px-4 py-2 rounded ${isBST ? "bg-green-600" : "bg-gray-700"}`}
        >
          BST Mode
        </button>
        <button
          onClick={() => setIsBST(false)}
          className={`px-4 py-2 rounded ${!isBST ? "bg-green-600" : "bg-gray-700"}`}
        >
          General Tree Mode
        </button>
      </div>

      {/* Input + Insert */}
      <div className="flex justify-center items-center gap-2 mb-4">
        <input
          type="text"
          value={value}
          onChange={(e) => setValue(e.target.value)}
          placeholder="Enter value"
          className="px-2 py-1 rounded text-black"
        />
        <button
          onClick={handleInsert}
          className="bg-blue-600 px-4 py-2 rounded hover:bg-blue-700"
        >
          Insert
        </button>
      </div>

      {/* Tree Rendering */}
      {renderTree(root)}
    </div>
  );
};

export default TreeVisualizer;
