import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

const StackVisualizer = () => {
  const [stack, setStack] = useState([]);
  const [inputValue, setInputValue] = useState("");

  const handlePush = () => {
    if (inputValue.trim() !== "") {
      setStack([...stack, inputValue]);
      setInputValue("");
    }
  };

  const handlePop = () => {
    if (stack.length > 0) {
      setStack((prev) => prev.slice(0, -1));
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900 text-white p-6">
      <h1 className="text-3xl font-bold mb-6">ðŸ§± Stack Visualizer</h1>

      <div className="flex gap-2 mb-6">
        <input
          type="text"
          className="px-3 py-2 text-black rounded-md focus:outline-none"
          placeholder="Enter value"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
        <button
          className="bg-green-600 hover:bg-green-700 px-4 py-2 rounded"
          onClick={handlePush}
        >
          Push
        </button>
        <button
          className="bg-red-600 hover:bg-red-700 px-4 py-2 rounded"
          onClick={handlePop}
        >
          Pop
        </button>
      </div>

      <div className="flex flex-col-reverse items-center gap-3 overflow-y-auto max-h-[60vh]">
        <AnimatePresence>
          {stack.map((item, index) => (
            <motion.div
              key={item + index}
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -30 }}
              transition={{ duration: 0.3 }}
              className={`w-40 text-center py-2 rounded shadow-md bg-blue-500 relative ${
                index === stack.length - 1 ? "border-4 border-yellow-400" : ""
              }`}
            >
              {item}
              {index === stack.length - 1 && (
                <div className="absolute -top-6 left-1/2 transform -translate-x-1/2 text-sm text-yellow-400">
                  Top
                </div>
              )}
            </motion.div>
          ))}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default StackVisualizer;
